let cartButtons=document.querySelectorAll(".add-to-cart"),cartModal=document.querySelector(".modal-cart");for(let t=0;t<cartButtons.length;t++)cartButtons[t].addEventListener("click",function(t){t.preventDefault(),document.body.style.overflow="hidden",cartModal.classList.add("modal-cart--show")});cartModal.addEventListener("click",function(t){t.target==cartModal&&(document.body.style.overflow="initial",cartModal.classList.remove("modal-cart--show"))});